//异步
function copyfile(copy1,copy2) {
    var fs = require("fs");
    fs.readFile(copy1,"",(err,data) => {
        console.log(data)
        fs.writeFile(copy2, data, {
            flag: "a"
        },(err) => {
            if (err) {
                console.error(err);
            } else {
                console.log("写入成功");
            }
        })

    })


}

//同步
function copyfileSync(copy1,copy2) {
   var fs = require("fs");
   var data = fs.readFileSync(copy1);
   fs.writeFileSync(copy2,data,{flag:"a"});
}

module.exports = {
    copyfile: copyfile,
    copyfileSync: copyfileSync
}